mapper.insert:
	INSERT
	  INTO TB_USER
	(
      USER_NO
	, ID
	, PWD
	, USER_NM
	, PHONE
	, ADDRESS
	, JOIN_DATE
	, SNS_TOKEN
	, EMAIL
	)
	VALUES
	(
	  'US'||(SEQ_USER.NEXTVAL)
	, #{ id }
	, #{ pwd }
	, #{ userNm }
	, #{ phone }
	, #{ address }
	, SYSDATE
	, #{ snsToken }
	, #{ email }
	)

mapper.update:
UPDATE
      TB_USER
  SET PHONE = #{ phone }
    , ADDRESS = #{ address }
WHERE USER_STATUS = 'Y'
  AND ID = #{ id }

mapper.select:
SELECT
       ID
  FROM TB_USER
 WHERE USER_NM = #{ userNm }
   AND PHONE = #{ phone }

mapper.insert:
INSERT INTO
         ORDER_ITEM
( ORDER_ITEM_NO
